import React, { Component } from 'react';
import { Layout, Menu, Breadcrumb } from 'antd';
import ReactDOM from 'react-dom';
import img1 from './images/1.jpg';
import img2 from './images/2.jpg';
import img3 from './images/3.jpg';
import img4 from './images/4.jpg';
import './home.css';
import styled from 'styled-components';
import { Input } from 'antd';
import { Carousel } from 'antd';
import { Steps, Button, message } from 'antd';






const Step = Steps.Step;
const Search = Input.Search;
const {
  Header, Footer, Sider, Content,
} = Layout;


const Container = styled.div`
  width: 100%;
  height: 60px;
  background-color: #2E9AFE;
  display: flex;
  flex-direction: row;
  justify-content: center;
`;

const LeftContainer = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: center;
  align-items: center;
  width: 30%;
  left: 40px;
`;

const RightContainer = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: right;
  align-items: center;
  width: 70%;
  
  
`;

const PageLink = styled.a`
  padding-right: 10px;  
  font-size: 20px;
  text-decoration: none;
  color: white;
  padding-right: 20px;
  margin-left: 65px;
  
`;


const steps = [{
  title: 'First',
  content: 'First-content',
}, {
  title: 'Second',
  content: 'Second-content',
}, {
  title: 'Last',
  content: 'Last-content',
}];

class Home extends Component {
  state = {
    current: ""
  };

  handleClick = e => {
    this.setState({
      current: e.key
    });
  };

  constructor(props) {
    super(props);
    this.state = {
      current: 0,
    };
  }

  next() {
    const current = this.state.current + 1;
    this.setState({ current });
  }

  prev() {
    const current = this.state.current - 1;
    this.setState({ current });
  }

  render() {
    const { current } = this.state;
    return (
      <div className="div">
      <Container>
        <LeftContainer>
        <PageLink href="/home" title="Tamaqtan" className="logo">TAMAQTAN</PageLink>    
        </LeftContainer>


        <RightContainer>    
          <PageLink href="/home" title="Главная">Главная</PageLink>      
          <PageLink href="#" title="Рецепты">Рецепты</PageLink>
          <PageLink href="#" title="Супер поиск">Супер поиск</PageLink>
          <PageLink href="#" title="Вход" className="vhod">Вход</PageLink>
        </RightContainer>
      </Container>

    
    <div className="search">
      <Search
      placeholder="Введите название блюда или ингредиента"
      title="Поиск"
      onSearch={value => console.log(value)}
      enterButton/>
    <br /><br />

    <Carousel autoplay>
      <div><img src={img1} className="img1"/></div>
      <div><img src={img2} className="img1"/></div>
      <div><img src={img3} className="img1"/></div>
      <div><img src={img4} className="img1"/></div>
    </Carousel>

    <Content >
    <Steps current={current} className="content">
          {steps.map(item => <Step key={item.title} title={item.title} />)}
        </Steps>
        <div className="steps-content">{steps[current].content}</div>
        <div className="steps-action">
          {
            current < steps.length - 1
            && <Button type="primary" onClick={() => this.next()}>Next</Button>
          }
        
          {
            current > 0
            && (
            <Button style={{ marginLeft: 8 }} onClick={() => this.prev()}>
              Previous
            </Button>
            )
          }
          </div>
    </Content>
    </div>  
    </div>


    
    
    );
  }
  
}

export default Home;
